INSERT IGNORE INTO Articles VALUES (10,'JIM BENYA!','<p>Free presentation in Eugene, sponsored by EWEB.  Hear Jim’s “news and reviews” from the world of lighting technology on  <strong>Wed.</strong> <strong>July 16th       7</strong>:00 - 9 :00PM at the U of O.  No charge but you must register. Click <a class="jce_file" title="jimbenya_flier_july16th.pdf" href="images/pdf_files/jimbenya_flier_july16th.pdf"> <img class="jce_icon" src="plugins/editors/jce/tiny_mce/plugins/filemanager/img/ext/pdf_small.gif" title="pdf" style="border:0px;vertical-align:middle;" /> jimbenya_flier_july16th.pdf</a> for more details.</p>','2008-07-08 23:15:12',NULL,4,1,'2008-07-09 07:15:12',0,580);
INSERT IGNORE INTO Articles VALUES (11,'The Sierra Club gives "thumbs up" support','<h4><a class="jcebox" target="_blank" title="power2change_2008report.jpg" href="images/stories/power2change_2008report.jpg"><img style="margin: 5px; float: right;" alt="power2change_2008report" src="images/stories/thumbnails/thumb_power2change_2008report.jpg" height="150" width="106" /></a>The Sierra Club gives a “thumbs up” to our 2-yr degree programs!</h4>
<p>See the article on their <a href="http://www.sierraclub.org/power2change/oregon/">Website</a> and the full page highlighting our programs in their newly released report “Power2Change: Energy Choices and the 2008 elections”</p>
<p>Download the article<a class="jce_file" title="power2change_2008report.pdf" href="images/pdf_files/power2change_2008report.pdf"> <img class="jce_icon" src="plugins/editors/jce/tiny_mce/plugins/filemanager/img/ext/pdf_small.gif" title="pdf" style="border:0px;vertical-align:middle;" /> power2change_2008report.pdf</a></p>
<blockquote>
<p>The Energy Management Program has expanded from a focus on residential energy efficiency to tackling the gargantuan need for commercial energy conservation solutions.</p>
</blockquote>','2008-07-08 23:17:55',NULL,17,1,'2008-07-10 23:17:55',0,7898);
